def cellar
  INPUT.chars.each_with_index.reduce(0) do |sum, (n, index)|
    break(index) if sum < 0

    if n == "("
      sum+1
    elsif n == ")"
      sum-1
    end
  end
end

def what_floor?
  INPUT.chars.reduce(0) do |sum, n|
    if n == "("
      sum+1
    elsif n == ")"
      sum-1
    end
  end
end

puts "Santa ends up on floor #{what_floor?} and he enters the cellar on"\
  " index #{cellar}"
